import { useLazyQuery } from '@apollo/client';
import { resetIdCounter, useCombobox } from 'downshift'
import gql from 'graphql-tag';
import { debounce } from 'lodash';
import { useRouter } from 'next/dist/client/router';
import { DropDown, DropDownItem, SearchStyles } from './styles/DropDown'

const SEARCH_PRODUCTS_QUERY = gql`
    query SEARCH_PRODUCTS_QUERY($searchTerm: String!){
        searchTerms: allProducts(
            where: {
                OR: [
                    {name_contains_i: $searchTerm},
                    {description_contains_i: $searchTerm}
                ]
            }
        ){
            id
            name
            photo{
                image{
                    publicUrlTransformed
                }
            }
        }
    }
`

export default function Search(){
    const router = useRouter()
   const [findItems, {loading, data, error} ] = useLazyQuery(SEARCH_PRODUCTS_QUERY, {
       fetchPolicy: 'no-cache'
   })
   const items = data?.searchTerms || []
   const findItemsButChill = debounce(findItems, 350)
    resetIdCounter();
   const { inputValue, getMenuProps, getInputProps, getComboboxProps, getItemProps, highlightedIndex, isOpen} = useCombobox({
       items,
       onInputValueChange() {
        findItemsButChill({
            variables:{
                searchTerm: inputValue,
            }
        });
       },
       onSelectedItemChange({selectedItem}){
        router.push({
            pathname: `/product/${selectedItem.id}`
        })
       }
   })
   return <SearchStyles>
        <div {...getComboboxProps()}>
            <input {...getInputProps({
                type: 'search',
                placeholder: 'Поиск...',
                className: loading ? `loading` : '',
            })} />
        </div>
        <DropDown {...getMenuProps}>
            {isOpen && items.map((item, index) => <DropDownItem key={item.id} {...getItemProps({item})} highlighted={index === highlightedIndex}>
                <img src={item.photo.image.publicUrlTransformed} alt={item.name} width={50} />
                {item.name}</DropDownItem>)}
            {isOpen && !items && !loading && (
                <DropDownItem>Извините, но ничего не найдено по запросу {inputValue}</DropDownItem>
            )}
        </DropDown>
    </SearchStyles>
}
