const formatMoney = (amount = 0) => {
    const options = {
        style: 'currency',
        currency: 'RUB',
        minimumFractionDigits: 2,

    }
    const formatter = Intl.NumberFormat('ru-RU', options)
    return formatter.format(amount)
}

export default formatMoney
